import React, {useState} from 'react';
import './App.css';
import CreateModal from './components/modals/CreateModal';
import TaskItem from './components/TaskItem';

function App() {
  const [tasks, setTasks] = useState([
    {time: "08:00", title: "Lick slicker finger"},
    {time: "10:00", title: "Start watch film"},
    {time: "18:30", title: "Disturp USSR"}
  ]);
  const [task, setTask] = useState({time: '', title: ''})
  const [modalActive, setModalActive] = useState(false)

  function addNewTask(e) {

  }

  return (
    <div className="App">
      <div className="Body">
        <div className="WrapperContent">
          <h1>TODO</h1>

          <hr className="MainLine"/>

          <div className="Tasks">
            {tasks.map((task) => 
              <TaskItem time={task.time} title={task.title} key={task.title}/>
            )}
          </div>
          <div className="Bottoms">
            <button className="Bottoms__create" onClick={() => setModalActive(true)}>+</button>
          </div>
        </div>
      </div>

      <CreateModal active={modalActive} setActive={setModalActive}>
        <div className="ModalBody">
          <form>
            <div className="ModalBody__inputs">
              <input 
                type="time" 
                placeholder="Time" 
                className="ModalBody__time" 
                onChange={e => setTask({...task, time: e.target.value})}/>
              <input 
                type="text" 
                placeholder="Task" 
                className="ModalBody__title" 
                onChange={e => setTask({...task, title: e.target.value})}/>
            </div>
            <button className="ModalBody__button" onClick={addNewTask(e)}>Create</button>
          </form>
        </div>
      </CreateModal>

    </div>
  );
}

export default App;
