import React, {useState, useMemo} from 'react';
import './App.css';
import CreateModal from './components/modals/CreateModal';
import TasksList from './components/TasksList';

function App() {
  const [tasks, setTasks] = useState([

  ]);
  const [task, setTask] = useState({time: '00:00', title: ''})
  const [modalActive, setModalActive] = useState(false)

  const addNewTask = (e) => {
    e.preventDefault()
    setTasks([...tasks, {...task, id: task.title}])
    setTask({time: '00:00', title: ''})
    setModalActive(false)
  }

    const sortedTasks = useMemo(() => {
      console.log(tasks)
      return [...tasks].sort((a,b) => a["time"].localeCompare(b["time"]))
    },[tasks])

  return (
    <div className="App">
      <div className="Body">
        <div className="WrapperContent">
          <h1>TODO</h1>

          <hr className="MainLine"/>

          {tasks ? 
            <TasksList 
            tasksList={sortedTasks}
          /> :
            <h3>Empty</h3>
          }
          <div className="Bottoms">
            <button className="Bottoms__create" onClick={() => setModalActive(true)}>+</button>
          </div>
        </div>
      </div>

      <CreateModal active={modalActive} setActive={setModalActive}>
        <div className="ModalBody">
          <form>
            <div className="ModalBody__inputs">
              <input 
                type="time" 
                value={task.time}
                placeholder="Time" 
                className="ModalBody__time" 
                onChange={e => setTask({...task, time: e.target.value})}/>
              <input 
                type="text" 
                value={task.title}
                placeholder="Task" 
                className="ModalBody__title" 
                onChange={e => setTask({...task, title: e.target.value})}/>
            </div>
            <button className="ModalBody__button" onClick={addNewTask}>Create</button>
          </form>
        </div>
      </CreateModal>

    </div>
  );
}

export default App;
